package com.kotlin.myapplication.uione.adapterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewabstract class BaseAdapter<T>(open var list: List<T>) : RecyclerView.Adapter<RecyclerView.ViewHolder>() {    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): RecyclerView.ViewHolder {        return onCreateViewHolderBase(parent, viewType)    }    override fun onBindViewHolder(holder: RecyclerView.ViewHolder, position: Int) {        onBindViewHolderBase(holder, position)    }    abstract fun onCreateViewHolderBase(parent: ViewGroup?, viewType: Int): RecyclerView.ViewHolder    abstract fun onBindViewHolderBase(holder: RecyclerView.ViewHolder?, position: Int)    override fun getItemCount(): Int {        return list.size    }    fun getData(): List<T> {        return list    }}