package com.kotlin.myapplication.uione.daoimport androidx.room.*import com.kotlin.myapplication.uione.model.Tag@Daointerface TagDao {    @Insert(onConflict = OnConflictStrategy.IGNORE)    suspend fun insertTag(tag: Tag)    @Query("select * from tag")    suspend fun getTags(): List<Tag>    @Query("DELETE FROM tag")    suspend fun deleteAll(): Int    @Query("DELETE FROM tag WHERE id =:id")    suspend fun delete(id: Int)    @Query("update tag set name = :name where id = :id")    suspend fun updateTag(name: String, id: Int)}