package com.kotlin.myapplication.uione.modelimport android.text.TextUtils.*import androidx.room.ColumnInfoimport androidx.room.Entityimport androidx.room.PrimaryKey@Entity(tableName = "tag")data class Tag(    @PrimaryKey(autoGenerate = true)    @ColumnInfo(name = "id")    var id: Int = 0,    @ColumnInfo(name = "name")    var name: String){    fun isVaild () : Boolean {        return !isEmpty(name)    }}