package com.kotlin.myapplication.uione.modelimport com.kotlin.myapplication.uione.dao.TagDaoclass TagRepository(val dao: TagDao) {    suspend fun addTag(tag: Tag) {        dao.insertTag(tag)    }    //truyen l√¢y theo tat ca    suspend fun getTags(): List<Tag> {        return dao.getTags()    }    suspend fun delete(id: Int) {         return dao.delete(id)    }    suspend fun updateTag(name: String, id: Int) {        return dao.updateTag(name, id)    }}