package com.kotlin.myapplication.uione.viewModelimport android.content.Contextimport androidx.appcompat.content.res.AppCompatResourcesimport androidx.databinding.BaseObservableimport androidx.databinding.ObservableArrayListimport com.kotlin.myapplication.Rimport com.kotlin.myapplication.uione.model.Menuclass MenuViewModel : BaseObservable() {    val menus: ObservableArrayList<Menu> = ObservableArrayList()    fun setupMenu(context: Context) {        val data = listOf(            Menu(AppCompatResources.getDrawable(context, R.drawable.ic_option_menu_1)!!),            Menu(AppCompatResources.getDrawable(context, R.drawable.ic_option_menu_2)!!),            Menu(AppCompatResources.getDrawable(context, R.drawable.ic_option_menu_3)!!),            Menu(AppCompatResources.getDrawable(context, R.drawable.ic_option_menu_4)!!),            Menu(AppCompatResources.getDrawable(context, R.drawable.ic_option_menu_5)!!),            Menu(AppCompatResources.getDrawable(context, R.drawable.ic_option_menu_6)!!),            Menu(AppCompatResources.getDrawable(context, R.drawable.ic_option_menu_7)!!),            Menu(AppCompatResources.getDrawable(context, R.drawable.ic_option_menu_8)!!),            Menu(AppCompatResources.getDrawable(context, R.drawable.ic_option_menu_9)!!),            Menu(AppCompatResources.getDrawable(context, R.drawable.ic_option_menu_9)!!)        )        this.menus.addAll(data)    }}