package com.kotlin.myapplication.uithree.adapterimport android.content.res.Resourcesimport androidx.fragment.app.Fragmentimport androidx.viewpager2.adapter.FragmentStateAdapterimport com.kotlin.myapplication.uithree.fragment.HomeFragmentimport com.kotlin.myapplication.uithree.fragment.ProfileFragmentimport com.kotlin.myapplication.uithree.fragment.SettingFragmentclass PagerAdapter(fragment: Fragment) : FragmentStateAdapter(fragment) {    override fun getItemCount() = 3    override fun createFragment(position: Int): Fragment {        return when (position) {            0 -> {                HomeFragment()            }            1 -> {                ProfileFragment()            }            2 -> {                SettingFragment()            }            else -> {                throw Resources.NotFoundException("Position Not Found")            }        }    }}