package com.kotlin.myapplication.uitwo.adapterimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.kotlin.myapplication.BRimport com.kotlin.myapplication.databinding.ItemShopListBindingimport com.kotlin.myapplication.uione.adapter.BaseAdapterimport com.kotlin.myapplication.uitwo.model.ShopListclass ShoplistAdapter : BaseAdapter<ShopList>(ArrayList()){    inner class MyViewModel(private val binding: ItemShopListBinding): RecyclerView.ViewHolder(binding.root){        fun bind(shopList: ShopList){            if(shopList.isDone){                binding.progressBar.progress = 5                binding.progressBar.max = 10                binding.tvComplete.text = "05/10"            }else {                binding.progressBar.progress = 3                binding.progressBar.max = 10                binding.tvComplete.text = "03/10"            }            binding.setVariable(BR.shopList, shopList)            binding.executePendingBindings()        }    }    override fun onCreateViewHolderBase(        parent: ViewGroup?,        viewType: Int    ): RecyclerView.ViewHolder {        val inflater = LayoutInflater.from(parent?.context)        val itemShopListBinding = ItemShopListBinding.inflate(inflater,parent,false)        return MyViewModel(itemShopListBinding)    }    override fun onBindViewHolderBase(holder: RecyclerView.ViewHolder?, position: Int) {        (holder as? MyViewModel)?.apply {            bind(list[position])        }    }}