package com.kotlin.myapplication.uitwo.fragmentimport android.os.Bundleimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.databinding.DataBindingUtilimport androidx.fragment.app.Fragmentimport androidx.recyclerview.widget.LinearLayoutManagerimport com.kotlin.myapplication.BRimport com.kotlin.myapplication.Rimport com.kotlin.myapplication.databinding.FragmentTestTwoBindingimport com.kotlin.myapplication.uitwo.adapter.ShoplistAdapterimport com.kotlin.myapplication.uitwo.adapter.ToDoListAdapterimport com.kotlin.myapplication.uitwo.viewmodel.UiMainViewModelclass TestTwoFragment : Fragment(){    lateinit var binding: FragmentTestTwoBinding    private val viewMode = UiMainViewModel()    override fun onCreateView(        inflater: LayoutInflater, container: ViewGroup?,        savedInstanceState: Bundle?    ): View? {        binding = DataBindingUtil.inflate(inflater, R.layout.fragment_test_two, container, false)        binding.setVariable(BR.viewModel, viewMode)        //noi goi fun        setupRecyclerShopList()        setupRecyclerTodoList()        return binding.root    }    private fun setupRecyclerShopList() {        viewMode.setupShopList(requireContext())        val adapter = ShoplistAdapter()        binding.recyclerViewShopList.adapter = adapter        binding.recyclerViewShopList.layoutManager =            LinearLayoutManager(requireContext(), LinearLayoutManager.VERTICAL, false)    }    private fun setupRecyclerTodoList() {        viewMode.setupTodoList(requireContext())        val adapter = ToDoListAdapter(requireContext() )        binding.recyclerViewTodoList.adapter = adapter        binding.recyclerViewTodoList.layoutManager =            LinearLayoutManager(requireContext(), LinearLayoutManager.VERTICAL, false)    }}