package com.kotlin.myapplication.uitwo.viewmodelimport android.content.Contextimport androidx.databinding.BaseObservableimport androidx.databinding.ObservableArrayListimport com.kotlin.myapplication.uitwo.model.ShopListimport com.kotlin.myapplication.uitwo.model.SubTaskimport com.kotlin.myapplication.uitwo.model.TodoListclass UiMainViewModel : BaseObservable() {    val toDoList: ObservableArrayList<TodoList> = ObservableArrayList()    val shopList: ObservableArrayList<ShopList> = ObservableArrayList()    fun setupShopList(context: Context) {        val data = listOf(            ShopList("Go to market", true),            ShopList("Go to school", false),            )        this.shopList.addAll(data)    }    fun setupTodoList(context: Context) {        val data = ArrayList<TodoList>()        val task = TodoList("Go to school")        val subTask = ArrayList<SubTask>()        subTask.add(            SubTask("apple")        )        subTask.add(            SubTask("banana" )        )        subTask.add(            SubTask("banana")        )        subTask.add(            SubTask("banana")        )        task.subTasks = subTask        data.add(            task        )        val toDos = TodoList("Go to market")        val subTasks = ArrayList<SubTask>()        subTasks.add(            SubTask("apple")        )        subTasks.add(            SubTask("banana" )        )        toDos.subTasks = subTasks        data.add(            toDos        )        data.add(            TodoList("Go to school")        )        this.toDoList.addAll(data)    }}